import { Fragment, useEffect, useState } from "react";
import React from "react";
import { Await, useNavigate } from "react-router-dom";
import "../SignInUp/Signin.css"
import { checkEmail, getSendCode } from "../../services/signInUp-service";
import Popup from "../Popup/Popup";
import EmailPhase from "./phases/email-phase";
import CodePhase from "./phases/code-phase";
import PasswordPhase from "./phases/password-phase";
import WelcomePhase from "./phases/welcome-phase";

const SignIn = () => {

    const [popupActive, setPopupActive] = useState(false);

    const [email, setEmail] = useState("email");
    const [inputCode, setInputCode] = useState("код");
    const [emailCode, setEmailCode] = useState(0);
    const [phase, setPhase] = useState(0);
    const [password, setPassword] = useState("пароль");

    const comp0 =  <EmailPhase phase={phase} setPhase={setPhase} email={email} setEmail={setEmail}></EmailPhase>
    const comp1 = <CodePhase phase={phase} setPhase={setPhase} code={inputCode} setCode={setInputCode}></CodePhase>
    const comp2 = <PasswordPhase phase={phase} setPhase={setPhase} password={password} setPassword={setPassword}></PasswordPhase>
    const comp3 = <WelcomePhase phase={phase} setPhase={setPhase}></WelcomePhase>

    const onSubmit = async (e, phase) =>{
        e.preventDefault()
        switch (phase) {
            case 0:
                if(email !=="email"){
                    if(await checkEmail(email)){
                        setPhase(phase + 2)
                    }else{
                        //console.log(await getSendCode(email));

                        setEmailCode(await getSendCode(email))
                        console.log(emailCode);
                        //setPhase((phase + 1))
                    }
                }
                break;
            case 1:
                console.log(emailCode);
                console.log(inputCode);
                break;
            case 2:
                
                break;
            case 3:
                
                break;
            default:
                break;
        }
    }

    const navigate = useNavigate();

    return(<Fragment>
        <form className="signInUp" onSubmit={e => onSubmit(e, phase)}>
            {phase===0?comp0:phase===1?comp1:phase===2?comp2:phase===3?comp3:navigate('/')}
        </form>
        <Popup active={popupActive} setActive={setPopupActive}>
            <a>try again</a>
        </Popup>
    </Fragment>);
};

export default SignIn;
