import { Fragment, useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import './Chats.css';
import { contextChats } from "../../../contexts/contextChats";
import defaultPhoto from '../../../img/masterPhoto.png'
import DotsImg from '../../../img/3-bots-img.png';

const Chats = () => {

    const params = useParams();

    const {chats, companionInfo} = useContext(contextChats);

    const [companionID, setCompanionID] = useState(params.with);
    const [messages, setMessages] = useState([]);

    useEffect(()=>{
        //console.log(params.email, params.id, params.with);
    },[companionID]);

    const click = (id) => {
        setCompanionID(id);
    }

    return(<Fragment>
        <div className='page-wrapper'>
            <div className="chats-content">
                <div className="messages-wrapper">
                    {params.with!=null?
                        <div className="message-wrapper" onClick={() => click(params.with)}>
                            <img src={companionInfo?.photo} alt=''></img>
                            <div> 
                                <h1>{companionInfo?.fio}</h1>
                            </div>
                        </div>
                    :
                        <></>}
                    {chats?.map((val, ind) => {
                        return(<div key={ind} className="message-wrapper" onClick={() => click(val?.id)}>
                            <img src={val?.photo} alt=""></img>
                            <div> 
                                <h1>{val?.fio}</h1>
                                <p>{val?.last_message}</p>
                            </div>
                        </div>);
                    })}
                </div>
                <div className="opened-chat">
                    <div className="chat-head">
                        <div>
                            {companionID}
                        </div>
                        <img src={DotsImg} alt=""></img>
                    </div>
                    <div className="chat-messages">
                        {messages?.map((val, ind) => {
                            if(val?.whose_message == params.email){
                                return(<div key={ind} className="chat-my-message">
                                        {val?.text.split(' ')?.map((v, i) => {
                                            return(<p>{v}</p>);
                                        })}
                                        {/*<p>{val?.date}</p>*/}
                                    </div>);
                            }else{
                                return(<div key={ind} className="chat-companion-message">
                                        {val?.text.split(' ')?.map((v, i) => {
                                            return(<p>{v}</p>);
                                        })}
                                        {/*<p>{val?.date}</p>*/}
                                    </div>);
                            }
                        })}
                    </div>
                    <div className="chat-input">
                        <input className="text-input-field" placeholder="Сообщение"></input>
                        <button className="button-grey" onClick={() => {}}>отправить</button>
                    </div>
                </div>
            </div>
        </div>
    </Fragment>);
}

export default Chats;
