import { useEffect, useState } from "react";
import masterController from "../../controllers/MASTER-controller";
import {Link} from "react-router-dom";
import MasterSmallCard from "./MasterSmallCard";

const ListOfMasters = ({topic, setActivePop, setTextPop}) => {

    const [list, setList] = useState();

    useEffect(() => {
        async function getListOfMasters(){
            setList(await masterController.getListOfMasters(0, 30));
        }
        getListOfMasters();
    }, []);

    const checkTopic = (value) => {
        var tf = false;
        value.forEach(element => {
            if(element[0].toLowerCase().includes(topic.toLowerCase())){
                tf = true;
            }
        });
        return tf;
    }   

    const showPhoto = async (path) => {
        return await masterController.getMasterPhotoByPath(path);
    };

    return(list?.map((value, index) => {
        if(checkTopic(value.sercicesAndPrice)){
            return(<MasterSmallCard index={index} value={value} setActivePop={setActivePop} setTextPop={setTextPop}></MasterSmallCard>);
        }else{
            return(<div>Не подходит</div>);
        }
    }));
}

export default ListOfMasters;
