import User from "../controllers/USER-controller";
import Topic from "../controllers/TOPIC-controller";
import masterPhoto from '../img/masterPhoto.png';

async function checkForAccessInCreateTopic(){

    if(localStorage.getItem('token')){
        const ret = await User.checkAuth();

        if(ret?.message){
            return false;
        }else{
            return true;
        }
    }
    return false;
}

async function createTopic({topic, FIO, phoneNumber, need, problem, problemLocation,
                                address, date, paymentOption, detailsText, detailsFiles, accountID}){
    
    const ret = await Topic.createNewTopic({topic, FIO, phoneNumber, need, problem, problemLocation,
        address, date, paymentOption, detailsText, detailsFiles, accountID});
        
    if(ret?.message){
        return false;
    }else{
        return true;
    }
}

async function getListOfExistingTopics(){
    const options = ["Сантехник ремонт", "Сантехник ремонт стояка",
                        "Сантехник онлайн", "Сантехник эксперт", 
                        "Сантехник срочно"];
    return options;
}

async function getListOfWork(){
    const options = ["Срочная помощь при аварии", "Прочистка канализации",
                        "Устранение течи", "Ремонт сантехники", 
                        "Установка или замена сантехники", "Демонтаж сантехники",
                        "Дистанционная консульация сантехника"];
    return options;
}

async function getListOfWhatHappend(){
    const options = ["Течь", "Засор", "Ржавчина"];
    return options;
}

async function getListofWhereIsProblem(){
    const options = ["Трубы, соединения труб", "Унитаз", "Раковина, мойка", "Ванна", "Душевая кабина",
                        "Полотенцесушитель", "Стояк", "Не знаю"];
    return options;
}

async function getListOfMasters(topic){
    const master = {name: 'Антон Горячев',
                    photo: masterPhoto,
                    stars: '4,8',
                    reviews: 297,
                    about: "Здравствуйте. Произвожу комплекс работ по сантехнике и электрике. От замены санфаянса, розеток, выключателей до прокладки коммуникаций. Буду рад вам помочь.",
                    experience: ["с 1999 г. (25 лет)"],
                    education: ["РосТех строй"],
                    sercicesAndPrice: [["Сантехнические работы", '2500 ₽/точка'], 
                                        ["Аварийные сантехники", '1500 ₽/усл.'],
                                        ["Замена смесителя", '700 ₽/шт.']],
                    reviewsExample: {stars: '5',
                                    date: '13 декабря 2023',
                                    from: 'Владислав',
                                    topic: 'Сантехника',
                                    text: 'Все хорошо и без лишних вопрос сделал задачу о которой мы договорились',
                                    price: '3000 ₽'}
                    };
    var masters = [];

    for (let index = 0; index < 30; index++) {
        masters.push(master);
    }

    return masters;
}

export {createTopic, checkForAccessInCreateTopic, 
    getListOfExistingTopics, getListOfWork, getListOfWhatHappend, 
    getListofWhereIsProblem, getListOfMasters};
