import { Fragment, useEffect, useState } from "react";
import "../UserProfile/UserProfile.css"
import { useNavigate } from 'react-router-dom'
import USERController from "../../controllers/USER-controller";
import MainInfoBlock from "./clientBlocks/MainInfoBlock";

const UserProfile = () => {

    const [user, setUser] = useState({});

    const navigate = useNavigate();

    const logout = async () => {
        if(await USERController.logOutUser()){
            navigate('/');
        }
    }

    useEffect(() => {
        async function setData(){
            setUser(await USERController.getUserInfo(localStorage.getItem('mail')));
        }

        setData();
    },[navigate]);

    return(<Fragment>
            <div className="user-profile">
                <div className='profile-wrapper'>
                    <div className='navigation-profile-wrapper'>
                        <button onClick={e => logout()} className='button-grey'>logOut</button>
                    </div>
                    <div className='profile-info'>
                        <MainInfoBlock photo_path={user?.photo_path} name={user?.dio} registration_date={user?.registration_date}></MainInfoBlock>
                        
                    </div>
                </div>
            </div>
        </Fragment>);
};

export default UserProfile;
