import React, { useRef, useEffect } from "react";
import { gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
import '../reviewSlider/reviewSlider.css'
gsap.registerPlugin(Draggable);

const pictures = [
  {topic: "Чистка ноутбука, замена термопасты",
   time: "Мастер нашелся за 15 мин",
   price: "1200 руб, оплата СБР",
   date: "5 декабря 2023 г.",
   master: {
      name: "Александр Н.",
      rate: "Рейтинг мастера: 4.5",
      stat: "Выполнил: 122 задания ",
      photo: "defult"
   }
  }
];

const Slide = ({ imageSource, content }) => {
  return (
    <div className="slide">
      <div className="preview">
        <img src={imageSource} alt="The Plant" draggable="false" />
      </div>
      <div className="infos">
        <h3>{content.date}</h3>
        <h2>{content.desc}</h2>
      </div>
    </div>
  );
};

export const Slider = () => {
  const sliderRef = useRef(null);

  useEffect(() => {
    console.log(sliderRef.current.clientWidth, sliderRef.current.innerWidth);
    Draggable.create(sliderRef.current, {
      type: "x",
      bounds: ".slider"
    });
  }, []);

  return (
    <body className="review">
        <div id="slider" className="slider" ref={sliderRef}>
          {pictures.map((item, index) => {
            return (
              <Slide key={index} imageSource={item.source} content={item.content} />
            );
          })}
      </div>
    </body>
  );
};
