import { Fragment, useEffect, useState } from "react";
import React from "react";
import { useNavigate } from "react-router-dom";
import "../SignInUp/Signin.css"

const SignIn = () => {

    const [login, setLogin] = useState("");
    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [phase, setPhase] = useState(1);

    const navigate = useNavigate();
    let comp = <phase1></phase1>;

    function phase1(){
        return(<div className="signIn">
        <div className="inner-signIn">
            <div className="annotation">
                <h1>
                Вход и регистрация 
                </h1>
                <a>
                Нажимая «Далее», вы соглашаетесьс Правилами сайта
                </a>
            </div>
            <div className="emailField">
                <input type="text" value={"email"} onChange={()=>{}}></input>
                <a>
                Специалисты не видят вашу почту. Вы сами решите, кому она будет доступена.
                </a>
            </div>
            <button className="buttonNext" onClick={setPhase(2)}>
            Продолжить
            </button>
            <div className="additionalEnter">
                <a>
                Ещё можно войти через соцсети
                </a>
                <div className="buttonsAdditionalEnter">
                    <button>

                    </button>
                    <button>

                    </button>
                    <button>

                    </button>
                    <button>

                    </button>
                </div>
            </div>
        </div>
    </div>);
    }

    function phase2(){
        return(<div>Hi</div>);
    }

    const onSubmit = async e => {
        /*e.preventDefault();
        try {
            const user = new User();
            
            user.logIn(login, password);
        } catch (error) {
            console.error(error);
        }*/
        navigate("/", {state:{email: login}})
    }

    useEffect(()=>{

        switch (phase) {
            case 1:
                comp = <phase1 />
                break;
            case 2:
                comp = <phase2 />
                break;
            default:
                break;
        }
    },[phase])
        
    return (<Fragment>
        <comp   />
    </Fragment>);
};

export default SignIn;
