import { Fragment, useEffect, useState } from "react";
import React from "react";
import { useNavigate } from "react-router-dom";
import "../SignInUp/Signin.css"
import { checkEmail } from "../../services/signInUp-service";
import Popup from "../Popup/Popup";
import EmailPhase from "./phases/email-phase";
import CodePhase from "./phases/code-phase";
import PasswordPhase from "./phases/password-phase";
import WelcomePhase from "./phases/welcome-phase";

const SignIn = () => {

    const [popupActive, setPopupActive] = useState(false);

    const [login, setLogin] = useState("");
    const [email, setEmail] = useState("");
    const [code, setCode] = useState("Код из сообщения")
    const [phase, setPhase] = useState(0);
    const [password, setPassword] = useState("пароль");

    /*const enter = {
        annotation: [{h1: "Вход и регистрация", a: "Нажимая «Далее», вы соглашаетесьс Правилами сайта"},
                        {h1: "Введите код из сообщения", a: "Код отправили на почту " + "email"},
                        {h1: "Введите пароль", a: ""}, {h1: "Вы успешно зарегистировались!", a: ""}],
        hint: [email, code, password]
    }

    const onSubmit = async e => {
        e.preventDefault();
        try {
            if(phase === 0){
                if(await checkEmail(email, phase)){
                    setPhase(phase + 2)
                }
                else{
                    setPhase(phase + 1)
                }
            }
            else if(phase === 1){
                setPhase(phase + 1)
            }
            else if(phase === 2){
                setPhase(phase + 1)
            }
            else if(phase === 3){
                navigate("/")
            }
        } catch (error) {
            console.error(error);
        }
    }
    
    return (<Fragment>
        <div className="signInUp">
        <div className="inner-signInUp">
        <div className="annotation">
            <h1>
                {enter.annotation[phase].h1}
            </h1>
            <a>
                {enter.annotation[phase].a}
            </a>
        </div>
        <div className="emailField">
            <input type="text" placeholder={enter.hint[phase]} onChange={e => setEmail(e.target.value)}></input>
            {phase === 0? 
            <a>Специалисты не видят вашу почту. Вы сами решите, кому она будет доступена.</a> 
            : <></>}
        </div>
        <button className="buttonNext" onClick={() => onSubmit}>
        Продолжить
        </button>
        {phase === 0? 
            <div className="additionalEnter">
            <a>
            Ещё можно войти через соцсети
            </a>
            <div className="buttonsAdditionalEnter">
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
            </div>
            </div>
            : <></>}
    </div>
    </div>
    <Popup active={popupActive} setActive={setPopupActive}>
        <a>try again</a>
    </Popup>
    </Fragment>);*/

    const navigate = useNavigate();

    const comp0 =  <EmailPhase phase={phase} setPhase={setPhase} email={email} setEmail={setEmail}></EmailPhase>
    const comp1 = <CodePhase phase={phase} setPhase={setPhase}></CodePhase>
    const comp2 = <PasswordPhase phase={phase} setPhase={setPhase}></PasswordPhase>
    const comp3 = <WelcomePhase phase={phase} setPhase={setPhase}></WelcomePhase>

    return(<Fragment>
        <div className="signInUp">
            {phase===0?comp0:phase===1?comp1:phase===2?comp2:phase===3?comp3:navigate('/')}
        </div>
        <Popup active={popupActive} setActive={setPopupActive}>
        <a>try again</a>
    </Popup>
    </Fragment>);
};

export default SignIn;
