export default class User{
    #SERVER_LOCATION="http://localhost:4000/api";
    isAuth = false;

    async #fetch(rout, method, body = {}, bearer = "", cookie = ""){
        let res;
        switch (method) {
            case "POST":
                if(bearer === ""){
                    res = await fetch((this.#SERVER_LOCATION + rout),{
                        method: method,
                        headers: {"Content-Type": "application/json"},
                        body: JSON.stringify(body)
                    })
                }else{
                    res = await fetch((this.#SERVER_LOCATION + rout),{
                        method: method,
                        headers: {'Authorization': bearer,"Content-Type": "application/json"},
                        withCredentials: true,
                        body: JSON.stringify(body)
                    })
                }
                break;
            case "GET":
                if(bearer === ""){
                    res = await fetch((this.#SERVER_LOCATION + rout),{
                        method: method,
                        headers: {"Content-Type": "application/json"},
                    })
                }else{
                    res = await fetch((this.#SERVER_LOCATION + rout),{
                        method: method,
                        headers: {'Authorization': bearer,"Content-Type": "application/json"},
                    })
                }
                break;
            default:
                break;
        }
        
        return res.json();
    };

    async registrate(login, email, photo, password){
        try {
            const rout = "/registration";
            const body = {"email": email, "login": login, "password": password};

            //const data = await this.#fetch(rout, "POST",  body);
            const data = await fetch((this.#SERVER_LOCATION + rout),{
                method: method,
                credentials: 'include',
                body: JSON.stringify(body)
            })
            const bot = data.json();
            localStorage.setItem('token', bot.refreshToken)

            return bot;
        } catch (error) {
            console.log("Error in registration:", error);
        }
    };

    async logIn(login, password){
        try {
            const rout = "/logIn";
            const body = {"login": login, "password": password};
            const data = await this.#fetch(rout, "POST", body);
            
            localStorage.setItem('token', data.refreshToken)
            console.log(data);
            this.isAuth = true;
            return data;
        } catch (error) {
            console.log("Error in logIn:", error);
        }
    }

    async logOut(){
        try {
            const rout = "/logOut";
            const data = await  this.#fetch(rout, "POST");

            localStorage.removeItem('token');

            return data;
        } catch (error) {
            console.log("Error in logOut:", error);
        }
    }
    
    async checkAuth(){
        try {
            const rout = "/refresh";
            //const data = await this.#fetch(rout, "GET");

            const data = await fetch("http://localhost:4000/api/refresh",{
                credentials: "include",
                method: "GET",
                headers: {
                    "Accept": "application/json",
                    "Content-Type": "application/json"}, 
            })
            const bot = await data.json();
            console.log(bot);
            localStorage.setItem('token', bot.accessToken)
            this.isAuth = true;
            
            return bot;
        } catch (error) {
            console.log("Error in checkAuth:", error);
        }
    }
}
