import { Fragment, useEffect, useState } from "react";
import React from "react";
import { useNavigate } from "react-router-dom";
import "../SignInUp/Signin.css"

const SignIn = () => {

    const [login, setLogin] = useState("");
    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");
    const [phase, setPhase] = useState(0);
    const enter = {
        annotation: [{h1: "Вход и регистрация", a: "Нажимая «Далее», вы соглашаетесьс Правилами сайта"},
                        {h1: "Введите код из сообщения", a: "Код отправили на почту " + "email"},
                        {h1: "Введите пароль", a: ""}, {h1: "Вы успешно зарегистировались!", a: ""}],
        hint: ["email", "Код из сообщения", "пароль"]
    }
    const navigate = useNavigate();

    const onSubmit = async e => {
        /*e.preventDefault();
        try {
            const user = new User();
            
            user.logIn(login, password);
        } catch (error) {
            console.error(error);
        }*/
        navigate("/", {state:{email: login}})
    } 

    useEffect(()=>{
        console.log(phase);
        if(phase === 3){
            navigate("/")
        }
    },[phase])

    return (<Fragment>
        <div className="signInUp">
        <div className="inner-signInUp">
        <div className="annotation">
            <h1>
                {enter.annotation[phase].h1}
            </h1>
            <a>
                {enter.annotation[phase].a}
            </a>
        </div>
        <div className="emailField">
            <input type="text" value={enter.hint[phase]} onChange={()=>{}}></input>
            {phase === 0? 
            <a>Специалисты не видят вашу почту. Вы сами решите, кому она будет доступена.</a> 
            : <></>}
        </div>
        <button className="buttonNext" onClick={()=>{setPhase(phase + 1)}}>
        Продолжить
        </button>
        {phase === 0? 
            <div className="additionalEnter">
            <a>
            Ещё можно войти через соцсети
            </a>
            <div className="buttonsAdditionalEnter">
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
            </div>
            </div>
            : <></>}
    </div>
    </div>
    </Fragment>);
};

export default SignIn;
