import { Fragment, useEffect, useState } from "react";
import Popup from "../Popup/Popup";
import '../CreateTopic/CreateTopic.css'
import checkForAccessInCreateTopic from "../../services/createTopic-services";
import {Stage1, Stage2, Stage3, Stage4, Stage5, Stage6, Stage7, Stage8, Stage9} from "./Stages";

const CreateTopic = () => {
    
    const [active, setActive] = useState(false)
    const [activeStage, setActiveStage] = useState(0);

    const comps = [<Stage1></Stage1>, <Stage2></Stage2>, 
                    <Stage3></Stage3>, <Stage4></Stage4>,
                    <Stage5></Stage5>, <Stage6></Stage6>, 
                    <Stage7></Stage7>, <Stage8></Stage8>, 
                    <Stage9></Stage9>]

    async function checkAccess(){
        setActive(await checkForAccessInCreateTopic());
    }

    useEffect(() => {
        checkAccess();
    }, [])

    return(<Fragment>
        <div className={active?"creatTopicWithAccess":"creatTopicWithOutAccess"}>
            <div className="content">
                <div className="left-content">
                    <button>
                        Специалисты
                    </button>
                    <button>
                        Помощь
                    </button>
                    <a>
                        Анкета-сантехник
                    </a>
                </div>
                <div className="right-content">
                    {comps[activeStage]}
                    <div className="control-buttons">
                        <button>
                            Назад
                        </button>
                        <button>
                            Продолжить
                        </button>
                    </div>
                </div>
            </div>
        </div>
        <Popup active={!active} setActive={setActive}>
            Войдите или зарегестрируйтесь, чтоб создавать темы
        </Popup>
    </Fragment>);
}

export default CreateTopic;
