import { checkEmail, getSendCode } from "../../../services/signInUp-service";
import {motion, useAnimate} from 'framer-motion'

const EmailPhase = ({phase, setPhase, email, setEmail, setEmailCode, errorInSingInUp, setErrorInSingInUp}) =>{ 

    const [scope, animate] = useAnimate();
    const [errorScope, animateError] = useAnimate();

    const errorMessages = ["Пароль должен содержать минимум одну заглавную букву, одну строчную букву, одну цифру и иметь длину не менее 8 символов. Пожалуйста, попробуйте еще раз."];

    const click = async () =>{
        if(await checkEmail(email)){
            setPhase(phase + 2);
        }else{
            var code = await getSendCode(email);
            if(code !== false){
                setEmailCode(code);
                setPhase(phase + 1);
            }else{
                animate(scope.current, 
                    {rotate: [0, 5, -5, 0], 
                        background: ["#DDDCDC", "#ff008c", "#DDDCDC"]}, 
                    {repeat: 1, duration: 0.5})
            }
        }
    };

    const showRegError = async (scale) => {
        await animateError(errorScope.current, {scale: scale});
    }

    return(<div className="inner-box" ref={scope}>
        <div className="annotationReg">
            <h1>
            Вход и регистрация
            </h1>
            <p>
            Нажимая «Далее», вы соглашаетесьс Правилами сайта
            </p>
        </div>
        <div className="inputField">
            <div className="inputAndError">
                <input type="text" placeholder={email} onChange={e => setEmail(e.target.value)}></input>
                {errorInSingInUp!==null?<div className="errorInSignInUp"
                onMouseEnter={() => showRegError(1)}
                onMouseLeave={() => showRegError(0)}>
                </div>: console.log("hi")}
                <motion.a className="errorRegMessage" ref={errorScope} initial={{scale: 0, y: 80, x: 542}}>{errorMessages[0]}</motion.a>
            </div>
            <a>Специалисты не видят вашу почту. Вы сами решите, кому она будет доступена.</a>
        </div>
        <button className="buttonNext" onClick={click}>
        Продолжить
        </button>
        <div className="additionalEnter">
            <a>
            Ещё можно войти через соцсети
            </a>
            <div className="buttonsAdditionalEnter">
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
            </div>
        </div>
    </div >);
}

export default EmailPhase;
