import { Fragment, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import './OrderPreview.css';
import { contextOrder } from '../../contexts/contextOrder';

const Order = () =>{

    const params = useParams;

    const pev_page = `${params.pev_page}/${params.who}`;

    const id_topic = params.id;

    const { order, setOrder } = useContext(contextOrder);

    useEffect(() => {
        console.log(order);
    }, [order])

    return(<Fragment>
        <div className="order">
            {order?.status == 'активен'?
            <div className="order-active">
                <h1>{order?.topic_name}</h1>
                <p>активен</p>
            </div>
            :order?.status == 'отменен'?
            <div className="order-cencelled">
                <h1>{order?.topic_name}</h1>
                <p>отменен</p>
            </div>
            :order?.status == 'завершен'?
            <div className="order-complite">
                <h1>{order?.topic_name}</h1>
                <p>завершен</p>
            </div>
            :<></>}
        </div>
    </Fragment>)
}

export default Order;
