import { useEffect, useState } from "react";
import masterController from "../../controllers/MASTER-controller";
import {Link} from "react-router-dom";
import MasterSmallCard from "./MasterSmallCard";

const ListOfMasters = ({topic, setActivePop, setTextPop}) => {

    const [list, setList] = useState();

    useEffect(() => {
        async function getListOfMasters(){
            setList(await masterController.getListOfMasters(0, 30));
        }
        getListOfMasters();
    }, []);

    const checkTopic = (value) => {
        var tf = false;
        value.forEach(element => {
            if(element[0].toLowerCase().includes(topic.toLowerCase())){
                tf = true;
            }
        });
        return tf;
    }   

    const showPhoto = async (path) => {
        return await masterController.getMasterPhotoByPath(path);
    };

    const sendMessage = () => {
        console.log("hi");
        setActivePop(true);
        setTextPop("Вы не можете писать специалистам, пока не заполните анкету");
    }

    const offerTopic = () => {
        setActivePop(true);
        setTextPop("Вы не можете предлагать специалистам, пока не заполните анкету");
    }

    const setStars = (value) => {
        var stars;
        if(value < 0.5){
            stars = '0';
        }if(value < 1){
            stars = '0-5';
        }else if(value < 1.5){
            stars = '1';
        }else if(value < 2){
            stars = '1-5';
        }else if(value < 2.5){
            stars = '2';
        }else if(value < 3){
            stars = '2-5';
        }else if(value < 3.5){
            stars = '3';
        }else if(value < 4){
            stars = '3-5';
        }else if(value < 4.5){
            stars = '4';
        }else if(value < 5){
            stars = '4-5';
        }else{
            stars = '5';
        }
        return `stars-${stars}`
    }

    return(list?.map((value, index) => {
        if(checkTopic(value.sercicesAndPrice)){
            return(<MasterSmallCard></MasterSmallCard>);
        }else{
            return(<div>Не подходит</div>);
        }
    }));
}

export default ListOfMasters;
