import { useEffect, useMemo, useRef, useState } from "react"
import { getListOfExistingTopics, getListOfWhatHappend, getListOfWork } from "../../services/createTopic-services"
import {YMaps, Map, Placemark, useYMaps, GeolocationControl, SearchControl} from "@pbe/react-yandex-maps";

const optionsOfExistingTopics = await getListOfExistingTopics();
const optionsOfWork = await getListOfWork();
const optionsOfWhatHappend = await getListOfWhatHappend();

const key = "52112b4d-5217-4897-8975-50bb62c674a6";
const lang = "ru_RU";

const Stage1 = () => {
    return(<div className="blockPhase">
        <h1>
            Как назвать задачу?
        </h1>
        <input defaultValue={"Сантехник"}></input>
        <div className="topicNameHints">
            <button>
            Сантехник
            </button>
            <button>
            Ремонт
            </button>
            <button>
            Срочно
            </button>
            <button>
            Дистанционно
            </button>
        </div>
    </div>)
}
const Stage2 = () => {
    return(<div className="blockPhase">
        <h1>
            Контактная информация
        </h1>
        <input defaultValue={"Имя Фамилия"}></input>
        <input defaultValue={"+7-000-000-00-00"}></input>
    </div>)
}
const Stage3 = () => {
    const [selectedOption, setSelectedOption] = useState(0);

    return(<div className="blockPhase">
        <h1>
            Что требуется?
        </h1>
        <div className="listOfOptions">
            {optionsOfWork.map((item, index) => {
                return(
                <div key={index} className="option">
                    <input type="radio"></input>
                    <p>{item}</p>
                </div>);
            })}
            <input type="radio"></input>
            <input></input>
        </div>
    </div>)
}
const Stage4 = () => {
    return(<div className="blockPhase">
        <h1>
            Что случилось?
        </h1>
        <input></input>
    </div>)
}
const Stage5 = () => {
    return(<div className="blockPhase">
        <h1>
            Где проблема?
        </h1>
        <input></input>
    </div>)
}
const Stage6 = () => {
    const [zoom, setZoom] = useState(9);
    const [center, setCenter] = useState([55.75, 37.57]);
    const [coordinate, setCoordinate] = useState([55.75, 37.57])
    const [address, setAddress] = useState(null);

    const ref = useRef();

    function setLocation(loc){
        console.log(loc);
        if(loc = "Spb"){
            setCoordinate([59.57, 30.19]);
            setCenter([59.57, 30.19]);
        }
    }

    return(<div className="blockPhase">
        <h1>
            По какому адресу?
        </h1>
        <input onChange={e => setLocation(e.target.value)}></input>
        <YMaps query={{apikey: key}}>
            <Map state={{center: center, zoom: zoom}} width={"862px"} height={"267px"}
            onLoad={(e) => {
                e.geocode(newCoords).then((res) => {
                    const firstGeoObject = res.geoObjects.get(0);
                    const newAddress = [
                        firstGeoObject.getLocalities().length
                            ? firstGeoObject.getLocalities()
                            : firstGeoObject.getAdministrativeAreas(),
                            firstGeoObject.getThoroughfare() || firstGeoObject.getPremise(),
                            firstGeoObject.getPremiseNumber()
                    ].filter(Boolean).join(", ");
                })
                setAddress(() => newAddress)
            }}>
                <Placemark geometry={coordinate}></Placemark>
            </Map>
        </YMaps>
    </div>)
}
const Stage7 = () => {
    return(<div className="blockPhase">
        <h1>
            Когда нужна услуга?
        </h1>
        <input></input>
    </div>)
}
const Stage8 = () => {
    return(<div className="blockPhase">
        <h1>
            Сколько готовы заплатить за работу?
        </h1>
        <input></input>
    </div>)
}
const Stage9 = () => {
    return(<div className="blockPhase">
        <h1>
            Остались пожелания к заказу?
        </h1>
        <input></input>
    </div>)
}
const Stage10 = () => {
    return(<div className="blockPhase">
        <h1>
            Выберете аккаунт для создания темы:
        </h1>
        <input></input>
    </div>)
}

export {Stage1, Stage2, Stage3, Stage4, Stage5, Stage6, Stage7, Stage8, Stage9, Stage10};
