import { Fragment, useEffect, useState } from "react";
import EditPopup from "../../EditPopup/EditPopup";
import userController from "../../../controllers/USER-controller";

const PersonalDataBlock = ({name, gender, email, phone_number}) =>{

    const [active, setActive] = useState(false);
    const [text, setText] = useState("");
    const [children, setChildren] = useState(<></>); 

    useEffect(()=>{
        console.log(text);
    }, [text])

    const update = () =>{
        setChildren(<div className='children-popup'>
            <h1 className="edit-popup-h">text</h1>
            <input className="edit-popup-input" onChange={e => setText(e.target.value)}></input>
        </div>);

        setActive(true)
    }


    return(<Fragment>
        <div className="phases-wrapper">
            <h1>Личные данные</h1>
            <div className="editable-block"> 
                <div className='edit-wrapper-button'>
                    <p>Имя</p>
                    <div className='edit-button' onClick={() => update()}></div>
                </div>
                <p>{name}</p>
            </div>
            <div className="editable-block">
                <div className='edit-wrapper-button'>
                    <p>Пол</p>
                    <div className='edit-button' onClick={() => update()}></div>
                </div>
                <p>{gender}</p>
            </div>
            <div className="editable-gray-block">
                <div className='edit-wrapper-button'>
                    <p>Электронная почта</p>
                    <div className='edit-button' onClick={() => update()}></div>
                </div>
                <p>{email}</p>
            </div>
            <div className="editable-gray-block">
                <div className='edit-wrapper-button'>
                    <p>Номер телефона</p>
                    <div className='edit-button' onClick={() => update()}></div>
                </div>
                <p>{phone_number}</p>
            </div>
        </div>
        <EditPopup active={active} setActive={setActive} 
        children={children}></EditPopup>
    </Fragment>);
}

export default PersonalDataBlock;
