import { Fragment, useContext, useEffect, useState } from "react";
import {observer} from "mobx-react-lite"
import { useLocation } from "react-router-dom";
import fetchWelcomeWords from "./MainpageFunctions";
import '../MainPage/Mainpage.css'
import gsap from 'gsap'
import {Draggable} from 'gsap/Draggable'

const MainPAge = () => {

    gsap.registerPlugin(Draggable);
    gsap.set(".slide", {
        backgroundColor: "random([red, blue, green, purple, orange, yellow, lime, pink])",
        xPercent: i => i * 100
    })


    const [words , setWords] = useState("");
    
    useEffect(() => {

        const fn1 = async () => {
            const w = await fetchWelcomeWords();
            setWords(w);
        }

        fn1();
        
    }, [])
    
    return(<Fragment>
        <body>
            <div>
                <text className="title">Повседневная</text>
                <text className="title">практика показывает</text>
                <text className="title">что рамки</text>
                <text className="title">место обучения</text>
            </div>
            <div className="reviews">
                <div class="slides-container">
                    <div class="slides-inner">
                        <div class="slide">1</div>
                        <div class="slide">2</div>
                        <div class="slide">3</div>
                        <div class="slide">4</div>
                        <div class="slide">5</div>
                        <div class="slide">6</div>
                        <div class="slide">7</div>
                        <div class="slide">8</div>
                        <div class="slide">9</div>
                        <div class="slide">10</div>
                    </div>
                </div>
            </div>
        </body>
    </Fragment>);

}

export default observer(MainPAge);
