const ButtonGoTo = () => {

    buttonHover = async (colour1, {buttonBG, animateButtonBG},colour2, {buttonText, animateButtonText}) => {
       await animateButtonBG(buttonBG.current, {backgroundColor: colour1});
       await animateButtonText(buttonText.current, {color: colour2});
   }

   showErrorHint = async(scale, {errorScope, animateError}) => {
       await animateError(errorScope.current, {scale: scale});
   }

    return(<div ref={buttonBG} className="toBeContinued"
                onMouseEnter={async () => await buttonHover("#2E2EB2", {buttonBG, animateButtonBG},
                                "#FFFFFF", {buttonText, animateButtonText})}
                onMouseLeave={async () => await buttonHover("#EBF0FF", {buttonBG, animateButtonBG},
                                "#3838CE", {buttonText, animateButtonText})}
                onClick={() => buttonOnClick("/CreateTopic")}>
        <a ref={buttonText}>Написать задание</a>
        <button></button>
    </div>);
}

export default ButtonGoTo;
