import { useState, useEffect } from "react";
import { motion } from "framer-motion";
import '../Loader/Loader.css';
import loadImg from '../../img/load.png';

const Loader = ({showLoader, setShowLoader}) => {
    useEffect(() => {
        setTimeout(() => {
            setShowLoader(false);
        }, Infinity);
    }, []);

    return(<div>
            {showLoader?
                <div className="loader">
                <motion.img src={loadImg} alt="It should be load image..." className="loader-img"
                    animate={{rotate: 360}}
                    transition={{repeat: Infinity, duration: 10, type: "tween"}}></motion.img>
                </div>
            :
                <></>}
         </div>);
}

export default Loader;
