import chatController from "../controllers/Chat-controller";

class Chat{
    #id_companion
    #id_chat
    #chats
    #messages
    constructor(){
        this.#id_companion = null;
        this.#chats = [];
        this.#id_chat = null;
        this.#messages = [];
    }

    async downloadChatsOfTopic(id_topic){
        this.#chats = await chatController.getChatsUserByIdTopic();
    }

    async downloadMessagesOfChat(id_topic){
        this.#messages = await chatController.getMessagesUser(id_topic, this.#id_companion);
    }

    async sendMessage(text, id_topic){
        if(text != ''){
            await chatController.sendMessageUser(text, this.#id_companion, id_topic);
        }
    } 

    get idCompanion(){
        return this.#id_companion;
    }
    set idCompanion(new_id_companion){
        if(new_id_companion != null){
            this.#id_companion = new_id_companion;
        }
    }
    get chats(){
        return this.#chats;
    }
    get idChat(){
        return this.#id_chat;
    }
    set idChat(new_id_chat){
        if(new_id_chat != null){
            this.#id_chat = new_id_chat;
        }
    }
    get messages(){
        return this.#messages;
    }
}

const chat = new Chat();

export default chat;
