import "./input-error-field.css";
import {motion, useAnimate} from 'framer-motion'
import buttonsAnimations from "./buttons-animations";

const InputWithError = ({placeholder, value, setValue, error}) => {
    
    const [errorScope, errorAnimate] = useAnimate();
    const [errorRed, animateErrorRed] = useAnimate();

    return(<div className="input-with-error">
        <input placeholder={placeholder} value={value} ref={errorRed} onChange={e => setValue(e.target.value)}></input>
        {error
            ?<div className="error-sign"
            onMouseEnter={() => buttonsAnimations.showErrorHint(1, {errorScope, errorAnimate})}
            onMouseLeave={() => buttonsAnimations.showErrorHint(0, {errorScope, errorAnimate})}>
            </div>
            : console.log("no_errors")}
        <motion.a className="error-message" ref={errorScope} initial={{scale: 0, y: 0, x: 650}}>Ошибка ввода</motion.a>
    </div>);
}

export default InputWithError;
