import { Fragment, useEffect, useState } from "react";
import masterController from "../../controllers/MASTER-controller";
//import {Link} from "react-router-dom";
import MasterProfileCard from "./MasterCard";

//const list = await masterController.getListOfMasters(0, 30);

const ListOfMasters = ({topic, setActivePop, setTextPop}) => {
    const [list, setList] = useState([]);

    useEffect(() => {
        var sortedList = [];

        async function setData(){
            setList(await masterController.getListOfMasters(0, 30));
        };

        setData();

        if(list.length > 0){
            list.forEach(el => {
                for (const val of el){
                    if(val[0].toLowerCase().includes(topic.toLowerCase())){
                        sortedList.push(el);
                        break;
                    };
                }
            });

            if(sortedList.length > 0){
                setList(sortedList); 
            }   
        }
        console.log(list);
    }, []);

    return(<Fragment>
        {list.map(obj => {
            return(<MasterProfileCard value={obj} setActivePop={setActivePop} setTextPop={setTextPop}></MasterProfileCard>);    
        })}
    </Fragment>);
}

export default ListOfMasters;
