class ButtonsAnimations{
    async buttonHover(colour1, colour2, {buttonBG, animateButtonBG}, {buttonText, animateButtonText}){
        animateButtonBG(buttonBG.current, {backgroundColor: colour1});
        animateButtonText(buttonText.current, {color: colour2});
    }
}

export default new ButtonsAnimations();
