import { useNavigate } from 'react-router-dom'
import "../Navigation/Navigation.css"
import { Fragment, useEffect, useState } from 'react';
import checkForAuth from '../../services/navigation-services';

const Navbar = () => {

    const navigate = useNavigate();

    const [auth, setAuth] = useState(null)
    const [search, setSearch] = useState("")

    async function name() {
        setAuth( await checkForAuth());
    }

    useEffect( () => {
        name();
    })

    const submitSearch = () => {
        navigate('/Search', {state:{search: search}})
    }

    return (<Fragment>
        <nav className="nav">
        <div className="inner-nav">
            <div className="rightPartOfNav">
                <a href='/' className='siteLogo'>починим.ру</a>
                <select>
                    <option>Санкт-Петербург</option>
                    <option>Москва</option>
                </select>
            </div>
            <div className="leftPartOfNav">
                <a href='/'>Мои заказы</a>
                <a href='/'>Форум </a>
                <select>
                    <option>Специалистам</option>
                    <option>чз</option>
                </select>
                <form onSubmit={submitSearch}>
                    <input className='navInput' onChange={e => setSearch(e.target.value)}></input>
                </form>
                <div className='acc'>
                    
                    {auth? <a href='/UserProfile'>{auth}</a> : <button onClick={()=> navigate("/SignInUp")}>Войти</button>}
                </div>
            </div>
        </div>
        </nav>
    </Fragment>);
};

export default Navbar
