import { Fragment, useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import './Chats.css';
import { contextChats } from "../../../contexts/contextChats";
import defaultPhoto from '../../../img/masterPhoto.png'
import DotsImg from '../../../img/3-bots-img.png';

const Chats = () => {

    const params = useParams();

    const {chats, setChats} = useContext(contextChats);
    const [messages, setMessages] = useState([]);

    useEffect(()=>{

        console.log(params.email, params.id, params.with);

        setChats([{'fio': 'Александр И.', 'total_star': 4.8, 'total_reviews': 297, 'last_message': 'Здравствуйте. Стоимость…'},
            {'fio': 'Александр И.', 'photo': defaultPhoto, 'total_star': 4.8, 'total_reviews': 297, 'last_message': 'Здравствуйте. Стоимость…'},
            {'fio': 'Александр И.', 'photo': defaultPhoto, 'total_star': 4.8, 'total_reviews': 297, 'last_message': 'Здравствуйте. Стоимость…'},
            {'fio': 'Александр И.', 'photo': defaultPhoto, 'total_star': 4.8, 'total_reviews': 297, 'last_message': 'Здравствуйте. Стоимость…'},
            {'fio': 'Александр И.', 'photo': defaultPhoto, 'total_star': 4.8, 'total_reviews': 297, 'last_message': 'Здравствуйте. Стоимость…'}])
        
        setMessages([{'whose_message': 'Александр Иванов', 'text':'fsvdfvsd', 'date': '14:30'},
            {'whose_message': 'apexlegaccforsale@gmail.com', 'text':'fvadfvsjg fjhgkghj khbjkghj kgh,jkmg,hjkfghjfghjfh khjklhjljl', 'date': '5 мин назад'}]);
    },[])

    return(<Fragment>
        <div className='page-wrapper'>
            <div className="chats-content">
                <div className="messages-wrapper">
                    {chats?.map((val, ind) => {
                        return(<div key={ind} className="message-wrapper">
                            <img src={val?.photo} alt=""></img>
                            <div> 
                                <h1>{val?.fio}</h1>
                                <p>{val?.last_message}</p>
                            </div>
                        </div>);
                    })}
                </div>
                <div className="opened-chat">
                    <div className="chat-head">
                        <div>
                            {params.with}
                        </div>
                        <img src={DotsImg} alt=""></img>
                    </div>
                    {messages?.map((val, ind) => {
                        if(val?.whose_message == params.email){
                            return(<div key={ind} className="chat-my-message">
                                    {val?.text.split(' ')?.map((v, i) => {
                                        return(<p>{v}</p>)
                                    })}
                                    <p>{val?.date}</p>
                                </div>);
                        }else{
                            return(<div key={ind} className="chat-companion-message">
                                    <p>{val?.text}</p>
                                    <p>{val?.date}</p>
                                </div>);
                        }
                    })}
                </div>
            </div>
        </div>
    </Fragment>);
}

export default Chats;
