import { Fragment, useEffect, useState } from "react";
import React from "react";
import { useNavigate } from "react-router-dom";
import "../SignInUp/Signin.css"
import { checkEmail } from "../../services/signInUp-service";
import Popup from "../Popup/Popup";

const SignIn = () => {

    const [popupActive, setPopupActive] = useState(false);

    const [login, setLogin] = useState("");
    const [email, setEmail] = useState("email");
    const [code, setCode] = useState("Код из сообщения")
    const [phase, setPhase] = useState(0);
    const [password, setPassword] = useState("пароль");

    const enter = {
        annotation: [{h1: "Вход и регистрация", a: "Нажимая «Далее», вы соглашаетесьс Правилами сайта"},
                        {h1: "Введите код из сообщения", a: "Код отправили на почту " + "email"},
                        {h1: "Введите пароль", a: ""}, {h1: "Вы успешно зарегистировались!", a: ""}],
        hint: [email, code, password]
    }
    const navigate = useNavigate();

    const onSubmit = async e => {
        e.preventDefault();
        try {
            if(phase === 0){
                if(email === ""){
                    setPopupActive(true)
                }
                else{
                    if(await checkEmail(email, phase)){
                        setPhase(phase + 2)
                    }
                    else{
                        setPhase(phase + 1)
                    }
                }
            }
            else if(phase === 1){
                setPhase(phase + 1)
            }
            else if(phase === 2){
                setPhase(phase + 1)
            }
            else if(phase === 3){
                navigate("/")
            }
            //setPhase(phase + 1)
        } catch (error) {
            console.error(error);
        }
    } 

    return (<Fragment>
        <div className="signInUp">
        <div className="inner-signInUp">
        <div className="annotation">
            <h1>
                {enter.annotation[phase].h1}
            </h1>
            <a>
                {enter.annotation[phase].a}
            </a>
        </div>
        <div className="emailField">
            <input type="text" placeholder={enter.hint[phase]} onChange={e => setEmail(e.target.value)}></input>
            {phase === 0? 
            <a>Специалисты не видят вашу почту. Вы сами решите, кому она будет доступена.</a> 
            : <></>}
        </div>
        <button className="buttonNext" onClick={onSubmit}>
        Продолжить
        </button>
        {phase === 0? 
            <div className="additionalEnter">
            <a>
            Ещё можно войти через соцсети
            </a>
            <div className="buttonsAdditionalEnter">
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
                <button>

                </button>
            </div>
            </div>
            : <></>}
    </div>
    </div>
    <Popup active={popupActive} setActive={setPopupActive}>
        <a>Hi</a>
    </Popup>
    </Fragment>);
};

export default SignIn;
