import { Fragment, useEffect, useState } from "react";
import Popup from "../Popup/Popup";
import '../CreateTopic/CreateTopic.css'
import checkForAccessInCreateTopic from "../../services/createTopic-services";
import {Stage1, Stage2, Stage3, Stage4, Stage5, Stage6, Stage7, Stage8, Stage9, Stage10} from "./Stages";

const CreateTopic = () => {
    
    const [active, setActive] = useState(false)
    const [activeStage, setActiveStage] = useState(0);
    const [formName, setFormName] = useState("сантехник");
 
    const comps = [<Stage1></Stage1>, <Stage2></Stage2>, 
                    <Stage3></Stage3>, <Stage4></Stage4>,
                    <Stage5></Stage5>, <Stage6></Stage6>, 
                    <Stage7></Stage7>, <Stage8></Stage8>, 
                    <Stage9></Stage9>, <Stage10></Stage10>]

    async function checkAccess(){
        setActive(await checkForAccessInCreateTopic());
    }

    useEffect(() => {
        checkAccess();
    }, [activeStage, comps])

    const moveOn = () => {
        const nextStage = activeStage + 1;
        if(nextStage <= comps.length - 1){
            setActiveStage(nextStage);
        }
        console.log(activeStage);
    }

    const moveBack = () => {
        const prevStage = activeStage - 1;
        if(prevStage >= 0){
            setActiveStage(prevStage);
        }
        console.log(activeStage);
    }

    return(<Fragment>
        <div className="creatTopic">
            <div className="content">
                <div className="left-content">
                    <div className="mastersAndNumber">
                        <button>
                            Специалисты
                        </button>
                        <a>1587</a>
                    </div>
                    <button>
                        Помощь
                    </button>
                    <a>
                    Заполнение формы - {formName}
                    </a>
                </div>
                <div className="right-content">
                    {activeStage<9?<a>{activeStage + 1}/9</a>:<></>}
                    {comps[activeStage]}
                    <div className="control-buttons">
                        <button onClick={moveBack}>
                            Назад
                        </button>
                        <button onClick={moveOn}>
                            Продолжить
                        </button>
                    </div>
                </div>
            </div>
        </div>
        <Popup active={!active} setActive={setActive}>
            Войдите или зарегестрируйтесь, чтоб создавать темы
        </Popup>
    </Fragment>);
}

export default CreateTopic;
